<?php
 switch (ENVIRONMENT) { case 'development': error_reporting(E_ALL | E_STRICT); break; case 'production': error_reporting(0); break; default: exit('The application environment is not set correctly.'); } function autoload_components($class_name) { $class_name = strtolower($class_name); $runtime_list = array( 'dispatcher', 'manager', 'data', 'data_adapter', 'global_functions', 'dump', 'utf8', 'i18n', 'cookie', 'session', 'mysql_adapter', 'mysqli_adapter', 'postgresql_adapter', 'sqlite_adapter' ); if (in_array($class_name, $runtime_list)) { if (SYS_RUNTIME_CACHE === TRUE) { $file = SYS_RUNTIME_DIR.'~'.$class_name.'.php'; if ( ! file_exists($file)) { file_put_contents($file, php_strip_whitespace(SYS_CORE_DIR.$class_name.'.php')); } } else { $file = SYS_CORE_DIR.$class_name.'.php'; } } else { $file = APP_MANAGER_DIR.$class_name.'.php'; } require_once $file; return; } spl_autoload_register('autoload_components'); function show_sys_error($heading, $message, $template = 'sys_error') { if (ENVIRONMENT === 'development') { ob_start(); require_once SYS_CORE_DIR.'sys_templates'.DS.$template.'.php'; $buffer = ob_get_contents(); ob_end_clean(); echo $buffer; exit; } } function dump($var, $force_type = '', $collapsed = FALSE) { new Dump($var, $force_type, $collapsed); } function __($string, array $values = NULL) { $string = I18n::get($string); return empty($values) ? $string : strtr($string, $values); } 